router.get('/', function (req, res, next) {
  res.render('orderlist', {
    title: 'Danh sch n hng'
  });
});
router.get('/create_payment_url', function (req, res, next) {
  var dateFormat = require('dateformat');

  var date = new Date();
  var desc = 'Thanh toan don hang thoi gian: ' + dateFormat(date, 'yyyy-mm-dd HH:mm:ss');
  res.render('order', {
    title: 'To mi n hng',
    amount: 10000,
    description: desc
  });
});
router.post('/create_payment_url', function (req, res, next) {
  var ipAddr = req.headers['x-forwarded-for'] || req.connection.remoteAddress || req.socket.remoteAddress || req.connection.socket.remoteAddress;

  var config = require('config');

  var dateFormat = require('dateformat');

  var tmnCode = config.get('7J3FP9LC');
  var secretKey = config.get('JOARTEJWKARRBHUVNBHNQDJYVWOMONRI');
  var vnpUrl = config.get('https://sandbox.vnpayment.vn/paymentv2/vpcpay.html');
  var returnUrl = config.get('http://localhost:3000/home');
  var date = new Date();
  var createDate = dateFormat(date, 'yyyymmddHHmmss');
  var orderId = dateFormat(date, 'HHmmss');
  var amount = req.body.amount;
  var bankCode = req.body.bankCode;
  var orderInfo = req.body.orderDescription;
  var orderType = req.body.orderType;
  var locale = req.body.language;

  if (locale === null || locale === '') {
    locale = 'vn';
  }

  var currCode = 'VND';
  var vnp_Params = {};
  vnp_Params['vnp_Version'] = '2.1.0';
  vnp_Params['vnp_Command'] = 'pay';
  vnp_Params['vnp_TmnCode'] = tmnCode; // vnp_Params['vnp_Merchant'] = ''

  vnp_Params['vnp_Locale'] = locale;
  vnp_Params['vnp_CurrCode'] = currCode;
  vnp_Params['vnp_TxnRef'] = orderId;
  vnp_Params['vnp_OrderInfo'] = orderInfo;
  vnp_Params['vnp_OrderType'] = orderType;
  vnp_Params['vnp_Amount'] = amount * 100;
  vnp_Params['vnp_ReturnUrl'] = returnUrl;
  vnp_Params['vnp_IpAddr'] = ipAddr;
  vnp_Params['vnp_CreateDate'] = createDate;

  if (bankCode !== null && bankCode !== '') {
    vnp_Params['vnp_BankCode'] = bankCode;
  }

  vnp_Params = sortObject(vnp_Params);

  var querystring = require('qs');

  var signData = querystring.stringify(vnp_Params, {
    encode: false
  });

  var crypto = require("crypto");

  var hmac = crypto.createHmac("sha512", secretKey);
  var signed = hmac.update(new Buffer(signData, 'utf-8')).digest("hex");
  vnp_Params['vnp_SecureHash'] = signed;
  vnpUrl += '?' + querystring.stringify(vnp_Params, {
    encode: false
  });
  res.redirect(vnpUrl);
});

