    id_tv: DataTypes.INTEGER,
    id_km: DataTypes.INTEGER,
    ngay_ban: DataTypes.STRING,
    giam_gia_ve: DataTypes.DECIMAL,
    trang_thai_ve: DataTypes.BOOLEAN
  }, {
    sequelize,
    modelName: 've_ban',
  });
  return ve_ban;
};

