sourcesContent:
require('dotenv').config();
import nodemailer from 'nodemailer';

let sendSimpleEmail = async (datasend) => {
    let transporter = nodemailer.createTransport({
        host: "smtp.gmail.com",
        port: 587,
        secure: false,
        auth: {
            user: process.env.EMAIL_APP,
            pass: process.env.EMAIL_APP_PASSWORD,
        },
    });

    let info = await transporter.sendMail({
        from: '"On Cinema" <oncinemasp@gmail.com>',
        to: datasend.receiverEmail,
        subject: "t v xem phim thnh cng!",
        html: `
        <h3>Xin cho ${datasend.name}</h3>
        <p>Bn  t v xem phim thnh cng trn On Cinema</p>
<p>Thng tin v:</p>
<div><b>Phim: ${datasend.movieName}</b></div>
<div><b>Sut chiu: ${datasend.showTime}</b></div>
<div><b>Ngy: ${datasend.date}</b></div>
<div><b>Rp: ${datasend.theater}</b></div>
<div><b>Gh: ${datasend.seat}</b></div>
<div><b>Thc n: ${datasend.food}</b></div>
<div><b>Tn PTTT: ${datasend.paymentName}</b></div>
<div>On Cinema chn thnh cm n bn! Chc bn xem phim vui v!!!</div>

        `
    });
}

async function main() {

}

module.exports = {
    sendSimpleEmail: sendSimpleEmail
}

