  rap.init({
    ten_rap: DataTypes.STRING,
    dia_chi: DataTypes.STRING,
    email: DataTypes.STRING,
    tinh_tpId: DataTypes.INTEGER
  }, {
    sequelize,
    modelName: 'rap',
  });
  return rap;
};

