        thanh_vien.hasMany(models.ve_ban, { foreignKey: 'id_tv', as: 'sdtData' }),
        thanh_vien.hasMany(models.hoa_don_thuc_an, { foreignKey: 'id_tv', as: 'sdtFood' })
    }
