import db from "../models/index"
import bcrypt from 'bcryptjs';


const salt = bcrypt.genSaltSync(10);

let handleUserLogin = (ten_tk, mat_khau) => {
    return new Promise(async (resolve, reject) => {
        try {
            let userData = {};
            let isExist = await checkTenTK(ten_tk);
            if (isExist) {
                //user aready exist
                let user = await db.Tai_Khoan.findOne({
                    where: { ten_tk: ten_tk },
                    raw: true
                });
                if (user) {
                    //compare password
                    let check = bcrypt.compareSync(mat_khau, user.mat_khau);
                    if (check) {
                        userData.errCode = 0;
                        userData.errMessage = 'Ok';
                        delete user.mat_khau;
                        userData.user = user;
                    } else {
                        userData.errCode = 3;
                        userData.errMessage = 'Sai mật khẩu';
                    }
                } else {
                    userData.errCode = 2;
                    userData.errMessage = 'Không tìm thấy tên tài khoản';
                }

            } else {
                //return error
                userData.errCode = 1;
                userData.errMessage = 'Tên tài khoản không tồn tại trong hệ thống!';

            }
            resolve(userData);
        } catch (e) {
            reject(e);
        }
    })
}

let handleAdminLogin = (ten_tk, mat_khau) => {
    return new Promise(async (resolve, reject) => {
        try {
            let userData = {};
            let isExist = await checkTenTK(ten_tk);
            if (isExist) {
                //user aready exist
                let user = await db.Tai_Khoan.findOne({
                    where: {
                        ten_tk: ten_tk,
                        role_id: 1 || 2
                    },
                    raw: true
                });
                if (user) {
                    //compare password
                    let check = bcrypt.compareSync(mat_khau, user.mat_khau);
                    if (check) {
                        userData.errCode = 0;
                        userData.errMessage = 'Ok';
                        delete user.mat_khau;
                        userData.user = user;
                    } else {
                        userData.errCode = 3;
                        userData.errMessage = 'Sai mật khẩu';
                    }
                } else {
                    userData.errCode = 2;
                    userData.errMessage = 'Không tìm thấy tên tài khoản';
                }

            } else {
                //return error
                userData.errCode = 1;
                userData.errMessage = 'Tên tài khoản không tồn tại trong hệ thống!';

            }
            resolve(userData);
        } catch (e) {
            reject(e);
        }
    })
}


let checkTenTK = (userTentk) => {
    return new Promise(async (resolve, reject) => {
        try {
            let user = await db.Tai_Khoan.findOne({
                where: { ten_tk: userTentk }
            })
            if (user) {
                resolve(true)
            } else {
                resolve(false)
            }
        } catch (e) {
            reject(e);
        }
    })
}

let checkEmail = (userEmail) => {
    return new Promise(async (resolve, reject) => {
        try {
            let user = await db.Nhan_Vien.findOne({
                where: { email: userEmail }
            })
            if (user) {
                resolve(true)
            } else {
                resolve(false)
            }
        } catch (e) {
            reject(e);
        }
    })
}

let getAllStaff = (staffId) => {
    return new Promise(async (resolve, reject) => {
        try {
            let staffs = '';
            if (staffId === 'ALL') {
                staffs = await db.Nhan_Vien.findAll({

                })
            }
            if (staffId && staffId !== 'ALL') {
                staffs = await db.Nhan_Vien.findOne({
                    where: { id: staffId }
                })
            }

            resolve(staffs)
        } catch (e) {
            reject(e);
        }
    })
}

let CreateNewStaff = (data) => {
    return new Promise(async (resolve, reject) => {
        try {
            // check email is exist
            let check = await checkEmail(data.email);
            if (check === true) {
                resolve({
                    errCode: 1,
                    errMessage: 'Email bạn nhập vào đã tồn tại trong hệ thống. Vui lòng nhập một email khác!'
                })
            } else {
                await db.Nhan_Vien.create({
                    ten_nv: data.ten_nv,
                    ngay_sinh: data.ngay_sinh,
                    gioi_tinh: data.gioi_tinh == '1' ? true : false,
                    email: data.email,
                    sdt: data.sdt,
                    dia_chi: data.dia_chi
                })
            }

            resolve({
                errCode: 0,
                message: 'OK'
            })
        } catch (e) {
            reject(e);
        }
    })
}


